import { useState } from "react";
import "./ImagesCard.css";

export const ImagesCard = ({ images }) => {
  const [imageShowing, setImageShowing] = useState(0);

  const handleImageChange = (event) => {
    console.log(event.target.value);
    const valueToBe =
      event.target.value === "prev" ? (imageShowing - 1) : (imageShowing + 1);
    setImageShowing(valueToBe);
  };
  return (
    <div className="images-card-container">
      <img src={images[imageShowing]} className="images-card" alt="" />
      <div className="images-card-control">
        <button
          value="previous"
          onClick={handleImageChange}
          className="images-card-change"
        >
          <i class="fas fa-arrow-left"></i>
        </button>
        <span>{imageShowing}</span>
        <button
          value="next"
          onClick={handleImageChange}
          className="images-card-change"
        >
          <i class="fas fa-arrow-right"></i>
        </button>
      </div>
    </div>
  );
};
