import { useContext } from "react";
import "./CartItemList.css"
import { CartContext } from "../../contexts/CartContext";
import { ProductContext } from "../../contexts/ProductsContext";

export const CartItemList = () => {
  const { cart, decreaseQuantity, increaseQuantity } = useContext(CartContext);
  const { products } = useContext(ProductContext)
  const testProducts = products.splice(0,3)
  return (
    <div>
      {
        testProducts.map(({id, name, price, quantity,thumbnail}) => 
          <div className="cart-list-products">
            <div className="cart-list-item-image">
              <img src={thumbnail} alt="" style={{width:"80px", height:"100px", borderRadius:"1rem", marginLeft:"1rem"}}/>
              <span className="cart-list-item-name">{name}</span>
            </div>
            <div>
              <div className="images-card-control">
                <button
                  value="previous"

                  className="images-card-change"
                >

                </button>
                <span>{quantity}</span>
                <button
                  value="next"


                >
                  <i class="fas fa-arrow-right"></i>
                </button>
              </div>
            </div>
          </div>
        )
      }
    </div>
  )
}
