import { useContext } from "react";
import "./CartItemList.css"
import { CartContext } from "../../contexts/CartContext";
import { ProductContext } from "../../contexts/ProductsContext";

export const CartItemList = () => {
  const { cart, decreaseQuantity, increaseQuantity } = useContext(CartContext);
  const { products } = useContext(ProductContext)
  const testProducts = products.splice(0,3)

  const handleQuantityChange = (event, itemId) => {
    console.log(event.target.id, itemId)
  }
  return (
    <div>
      {
        cart.map(({id, name, price, quantity,thumbnail}) => 
          <div className="cart-list-products">
            <div className="cart-list-item-image">
              <img src={thumbnail} alt="" style={{width:"80px", height:"100px", borderRadius:"1rem", marginLeft:"1rem"}}/>
              <span className="cart-list-item-name">{name}</span>
            </div>
            <div className="cart-list-price-and-quantity">
              <div className="quantity-control">
                <button value="previous" onClick={(event) => handleQuantityChange(event, id)}>
                  <i class="fas fa-arrow-left"></i>
                </button>
                <span className="quantity-text">{quantity}</span>
                <button value="next" onClick={(event) => handleQuantityChange(event, id)}>
                  <i class="fas fa-arrow-right"></i>
                </button>
              </div>
              <span className="cart-list-item-multiplier">x</span> 
              <span className="cart-list-item-price">{price}</span>
              <span className="cart-list-item-multiplier">=</span>
              <span className="cart-list-item-price">{quantity * price}</span>
            </div>
          </div>
        )
      }
    </div>
  )
}
