import { useContext, useState } from "react";
import { CartContext } from "../../contexts/CartContext";
import { NavLink } from "react-router-dom";

import "./header.css"

export const Header = () => {
  const [isCartOpen, setIsCartOpen] = useState(false)
  const { cart } = useContext(CartContext);

  const getActiveStyles = ({ isActive }) => ({
    padding: "0.5rem 0.7rem",
    color: isActive ? "black" : "",
    backgroundColor: isActive ? "#d0cbb9" : "",
    borderRadius: isActive ? "1rem" : ""
  });

  const cartItemsTotal = cart.reduce((acc, curr) => acc + curr.quantity, 0);
  return (
    <div className="nav-container">
      <div>
        <h1 className="nav-title">Weird Choice</h1>
      </div>
      <div className="nav-right-side">
        <nav className="nav-items">
          <NavLink className="nav-item" to="/" style={getActiveStyles}>
            Home
          </NavLink>{" "}
          <NavLink className="nav-item" to="/cart" style={getActiveStyles}>
            Cart
          </NavLink>
        </nav>
        <button className={`cart-icon ${isCartOpen ? "cart-open" : ""}`} onClick={() => setIsCartOpen(!isCartOpen)}>
        {cart.length !== 0 && <div className="cart-item-count"><span className="cart-item-count-text">{cartItemsTotal}</span></div>} <i class="fas fa-cart-shopping"></i>
        {isCartOpen && (
          <div className="cart-details">
            <table className="cart-details-table">
              <tr>
                <th>Product</th>
                <th></th>
                <th>Total</th>
              </tr>
              {
                cart.map(({id, name, quantity, price}) =>                       
                  <tr key={id}>
                    <td>{name}</td>
                    <td>{quantity} x {price}</td>
                    <td>{quantity * price}</td>
                  </tr>
                )
              }
            </table>
          </div>
        )}
        </button>
      </div>
    </div>
  );
};
