import { useContext } from "react"
import { newProducts, products } from "../../products/productsDB"
import { ProductCard } from "../productCard/ProductCard"

import "./ProductList.css"
import { ProductContext } from "../../contexts/ProductsContext"
import { filterByValueHelper } from "../../utils/filterAndSortHelper"

export const ProductList = () => {
  const { products, filterValue, sortValue, categoryValue } = useContext(ProductContext)

  let filteredProducts = []
  if(filterValue){
    console.log('in filter')
    filteredProducts = filterByValueHelper(products, filterValue)
  }
  if(sortValue){
    console.log('in sort')
    if(sortValue==="lowToHigh"){
      filteredProducts = [...products].sort((a,b) => a.price-b.price)
    }else if(sortValue==="highToLow") {
      filteredProducts = [...products].sort((a,b) => b.price-a.price)
    }else if(sortValue==="default") {
      filteredProducts = [...products]
    }
  }

  if(categoryValue){
    filteredProducts = products.filter(({category}) => category === categoryValue)
  }

  if(!filterValue && !sortValue && !categoryValue){
    console.log('in filter')
    filteredProducts = [...products]
  }

  return (
    <div className="product-list-container">
      {
        filteredProducts.map(product => 
          <ProductCard key={product.id} product={product}/>   
        )
      }
    </div>
  )
}
